import React, { useState } from 'react';
import './submitted.css'; // This file will contain necessary styles
import Footer from './footer';

const Submitted = () => {
    const [formSubmitted, setFormSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        setFormSubmitted(true); // Set the form as submitted
    };

    return (
        <div className="submittedPage">
            <header className="header">
                <img src="logo.png" alt="MSU-IIT NMPC Logo" className="logo" />
                <h2 className="landingTitle">MSU-IIT National Multi-Purpose Cooperative</h2>
            </header>

            <div className="submittedFormContainer">
                {!formSubmitted ? (
                    <>
                        <h3 className="formTitle">Cash Flow Statement</h3>
                        <form onSubmit={handleSubmit} className="cashflowForm">
                            {/* Form fields for Income, Expenditures, and Cash Outlays go here */}
                            <button type="submit" className="submitButton">Submit</button>
                        </form>
                    </>
                ) : (
                    <div className="confirmationOverlay">
                        <div className="confirmationMessage">
                            <h3>You've submitted your application.</h3>
                            <p>NAGPA-SA CO-MAKER</p>
                            <button onClick={() => setFormSubmitted(false)} className="closeButton">Close</button>
                        </div>
                    </div>
                )}
            </div>

            <Footer />
        </div>
    );
};

export default Submitted;
