import React, { useState } from 'react';
import './submitted.css'; // This file will contain necessary styles
import Footer from './footer';

const Submitted = () => {
    const [formSubmitted, setFormSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        setFormSubmitted(true); // Set the form as submitted
    };

    return (
        <div className="submittedPage">
            <header className="header">
                <img src="logo.png" alt="MSU-IIT NMPC Logo" className="logol" />
                <h2 className="landingh2off2">MSU-IIT National Multi-Purpose Cooperative</h2>
            </header>

            <div className="submittedFormContainer">
                {!formSubmitted ? (
                    <>
                        <h3 className="title">Cash Flow Statement</h3>
                        <form onSubmit={handleSubmit} className="cashflow">
                            {/* Your form structure (same as in the CashFlow component) */}
                            {/* All the income, expenditure, and cash outlay sections */}
                            <button type="submit" className="submit-btn-1">Submit</button>
                        </form>
                    </>
                ) : (
                    <div className="confirmationMessage">
                        <h3>Thank you for your submission!</h3>
                        <p>Your Cash Flow Statement has been successfully submitted.</p>
                    </div>
                )}
            </div>

            <Footer />
        </div>
    );
};

export default Submitted;
