import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './borrowerdash.css';

const BorrowerHeader = () => {
  const [showNotifications, setShowNotifications] = useState(false);

  const notifications = [
    "You only have 3 weeks before your due date.",
    "The funds have been deposited in your account. Congratulations!",
    "Your application is now being reviewed.",
    "Your application has been approved.",
  ];

  const toggleNotifications = () => {
    setShowNotifications(!showNotifications);
  };

  return (
    <header className="headdash">
      <img src="logo.png" alt="MSU-IIT NMPC Logo" className="logol" />
      <h2 className="dashh2">MSU-IIT National Multi-Purpose Cooperative</h2>
      <nav className="iconn">
        <Link to="/dashboard">
          <img src="Home.png" alt="Home" className="navicon1" />
        </Link>
        <div className="notification-icon">
          <img
            src="Bell_pin.png"
            alt="Notifications"
            className="navicon"
            onClick={toggleNotifications}
          />
          {showNotifications && (
            <div className="notification-dropdown">
              <h3>15 Notifications</h3>
              {notifications.map((note, index) => (
                <p key={index} className="notification-item">
                  {note}
                </p>
              ))}
            </div>
          )}
        </div>
        <Link to="/profile">
          <img src="User_circle.png" alt="Profile" className="navicon" />
        </Link>
      </nav>
      <span>Charles Deo</span>
    </header>
  );
};

export default BorrowerHeader;
