import React from 'react';
import { Line, Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend);

const Graphs = () => {
  // Sample data for each graph
  const data = [12, 19, 3, 5, 2, 3];
  const labels = ['January', 'February', 'March', 'April', 'May', 'June'];

  // Line chart data
  const lineData = {
    labels,
    datasets: [
      {
        label: 'Monthly Revenue',
        data,
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  // Bar chart data
  const barData = {
    labels,
    datasets: [
      {
        label: 'Monthly Loan Applications',
        data,
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1
      }
    ]
  };

  // Pie chart data
  const pieData = {
    labels: ['Approved', 'Pending', 'Rejected'],
    datasets: [
      {
        label: 'Loan Status Distribution',
        data: [45, 25, 30],
        backgroundColor: ['rgba(75, 192, 192, 0.5)', 'rgba(255, 206, 86, 0.5)', 'rgba(255, 99, 132, 0.5)'],
        borderColor: ['rgba(75, 192, 192, 1)', 'rgba(255, 206, 86, 1)', 'rgba(255, 99, 132, 1)'],
        borderWidth: 1
      }
    ]
  };

  return (
    <div className="graphs-container">
      <h3>Data Visualizations</h3>
      <div className="graph">
        <h4>Revenue Over Time</h4>
        <Line data={lineData} options={{ responsive: true }} />
      </div>
      <div className="graph">
        <h4>Loan Applications by Month</h4>
        <Bar data={barData} options={{ responsive: true }} />
      </div>
      <div className="graph">
        <h4>Loan Status Distribution</h4>
        <Pie data={pieData} options={{ responsive: true }} />
      </div>
    </div>
  );
};

export default Graphs;
