import React, { useState } from 'react';
import './quizresult.css';
import Footer from './footer';

const QuizResult = () => {
    // Modal states for each loan type
    const [isEducationalModalOpen, setIsEducationalModalOpen] = useState(false);
    const [isPersonalModalOpen, setIsPersonalModalOpen] = useState(false);
    const [isPensionerModalOpen, setIsPensionerModalOpen] = useState(false);

    // Functions to open each modal
    const openEducationalModal = () => setIsEducationalModalOpen(true);
    const openPersonalModal = () => setIsPersonalModalOpen(true);
    const openPensionerModal = () => setIsPensionerModalOpen(true);

    // Function to close all modals
    const closeModal = () => {
        setIsEducationalModalOpen(false);
        setIsPersonalModalOpen(false);
        setIsPensionerModalOpen(false);
    };

    return (
        <div className="quiz-result">
            {/* Header Section */}
            <header className="header">
                <img src="logo.png" alt="MSU-IIT NMPC Logo" className="logol" />
                <h2 className="landingh2off2">MSU-IIT National Multi-Purpose Cooperative</h2>
            </header>

            {/* Congratulatory Banner */}
            <div className="congrats-banner">
                <img src="cong.png" alt="Congratulations Banner" className="congrats-image" />
            </div>

            {/* Quiz Result Section */}
            <div className="main-container">
                <div className="result-container">
                    <p className="greeting">Dear Charles Deo,</p>
                    <p className="qualification-message">
                        Congratulations! You’re qualified for a personal loan.
                    </p>
                    <button className="apply-loan-button">Apply personal loan</button>

                    <h3>Here’s some loan offers you can avail to:</h3>

                    {/* Loan Offers */}
                    <div className="loan-offers">
                        <div className="loan-card" onClick={openEducationalModal}>
                            <img src="educ.jpg" alt="Educational Loan" className="loan-image" />
                            <h4>Educational Loan</h4>
                        </div>
                        <div className="loan-card" onClick={openPersonalModal}>
                            <img src="personal.jpg" alt="Personal Loan" className="loan-image" />
                            <h4>Personal Loan</h4>
                        </div>
                        <div className="loan-card" onClick={openPensionerModal}>
                            <img src="pens.jpg" alt="Pensioner Loan" className="loan-image" />
                            <h4>Pensioner Loan</h4>
                        </div>
                    </div>

                    <div className="button-container">
                        <button className="quiz-again-button">Take Quiz Again</button>
                    </div>
                </div>
            </div>

            {/* Modals */}
            {isEducationalModalOpen && (
                <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <button className="close-modal" onClick={closeModal}>&times;</button>
                        <h2>Educational Loan</h2>
                        <p>Get a loan of up to P100,000 to support your educational needs.</p>
                        <p><strong>Interest Rate:</strong> 3% per annum</p>
                        <p><strong>Term:</strong> Up to 12 months</p>
                        <button className="apply-button">Apply Now</button>
                    </div>
                </div>
            )}

            {isPersonalModalOpen && (
                <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <button className="close-modal" onClick={closeModal}>&times;</button>
                        <h2>Personal Loan</h2>
                        <p>Access a personal loan of up to P50,000 for your individual needs.</p>
                        <p><strong>Interest Rate:</strong> 5% per annum</p>
                        <p><strong>Term:</strong> Up to 24 months</p>
                        <button className="apply-button">Apply Now</button>
                    </div>
                </div>
            )}

            {isPensionerModalOpen && (
                <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <button className="close-modal" onClick={closeModal}>&times;</button>
                        <h2>Pensioner Loan</h2>
                        <p>Designed for retirees, loan amounts of up to P70,000 are available.</p>
                        <p><strong>Interest Rate:</strong> 4% per annum</p>
                        <p><strong>Term:</strong> Up to 18 months</p>
                        <button className="apply-button">Apply Now</button>
                    </div>
                </div>
            )}

            <Footer />
        </div>
    );
};

export default QuizResult;
